var NotesLists = new Vue({
  el: "#root",
  data: {
    notes: [],
    tableView: true,
    loader: true,
    filter: "",
  },
  mixins: [mixins],
  computed: {
    filterNotes: function () {
      if (!!this.filter) {
        let filterTerm = this.filter.toLowerCase();
        return this.notes.filter((value, index) => {
          return this.searchInObject(value, filterTerm);
        });
      } else {
        return this.notes;
      }
    },
  },
  methods: {
    getcontentText: function (note) {
      return note.content.substring(0, 50) + "...";
    },
    toggleView: function () {
      this.tableView = !this.tableView;
      this.initPlugins();
    },
    resetFilter: function () {
      this.filter = "";
    },
    getPageImagePath(note) {
      if (note.imagen_file) {
        return (
          BASEURL +
          note.imagen_file.file_path.substr(2) +
          note.imagen_file.file_name +
          "." +
          note.imagen_file.file_type
        );
      }
      return BASEURL + "public/img/default.jpg";
    },
    getNotes: function () {
      var self = this;
      $.ajax({
        type: "GET",
        url: BASEURL + "api/v1/notes/",
        data: {},
        dataType: "json",
        success: function (response) {
          let notes = response.data;
          for (const key in notes) {
            if (notes.hasOwnProperty(key)) {
              notes[key].user = new User(notes[key].user);
            }
          }
          self.notes = notes;
          setTimeout(() => {
            self.loader = false;
            self.initPlugins();
          }, 1000);
        },
        error: function (error) {
          M.toast({ html: response.responseJSON.error_message });
          self.loader = false;
        },
      });
    },
    delete: function (note, index) {
      var self = this;
      self.loader = true;
      $.ajax({
        type: "DELETE",
        url: BASEURL + "api/v1/notes/" + note.note_id,
        data: {},
        dataType: "json",
        success: function (response) {
          if (response.code == 200) {
            self.notes.splice(index, 1);
          }
          setTimeout(() => {
            self.loader = false;
            self.initPlugins();
          }, 1000);
        },
        error: function (error) {
          M.toast({ html: response.responseJSON.error_message });
          self.loader = false;
        },
      });
    },
    tempDelete: function (item, index) {
      this.toDeleteItem.item = item;
      this.toDeleteItem.index = index;
    },
    confirmCallback(data) {
      if (data) {
        this.delete(this.toDeleteItem.item, this.toDeleteItem.index);
      }
    },
    base_url: function (path) {
      return BASEURL + path;
    },
    initPlugins: function () {
      setTimeout(() => {
        var elems = document.querySelectorAll(".tooltipped");
        M.Tooltip.init(elems, {});
        var elems = document.querySelectorAll(".dropdown-trigger");
        M.Dropdown.init(elems, {});
      }, 3000);
    },
  },
  mounted: function () {
    this.$nextTick(function () {
      this.getNotes();
      this.initPlugins();
    });
  },
});
