var fileExplorerModule=new Vue({el:"#root",data:{loader:!1,files:[],recentlyFiles:[]},computed:{getFolders(){return this.files.filter(e=>"folder"==e.file_type)},getFiles(){return this.files.filter(e=>"folder"!=e.file_type)}},methods:{getIcon(e){let t="description";switch(e.file_type){case"folder":t="folder";break;case"img":case"png":case"gif":t="insert_photo";break;case"html":t="web"}return t},getExtention:e=>"folder"==e.file_type?"":"."+e.file_type},filters:{shortName:function(e){return e?(e=e.toString()).length>15?e.substr(0,15)+"...":e.substr(0,15):""}},mounted:function(){this.$nextTick((function(){var e=this;$.ajax({type:"POST",url:BASEURL+"admin/archivos/ajax_get_files",data:{path:"./"},dataType:"json",success:function(t){200==t.code&&(e.files=t.data)}})}))}});